import './ExpenseForm.css';
import { useState } from 'react';

const ExpenseForm = ()=>{
    const [title,setTitle] = useState("");
    const titleChangeHandler = (ele)=>{
        setTitle(ele.target.value);
    };
    const [price,setPrice] = useState("");
    const priceChangeHandler = (ele)=>{
        setPrice(ele.target.value);
    };
    const [date,setDate] = useState("");
    const dateChangeHandler = (ele)=>{
        setDate(ele.target.value);
    };
    const  newExpenseObject = {
        title : title,
        price : price,
        date  : date
    };
    console.log(newExpenseObject);
return(
        <form>
            <div className="new-expense_controls">
                <div className="new-expense_control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler} />
                </div>
                <div className="new-expense_control">
                    <label>Date</label>
                    <input type="date" onChange={dateChangeHandler}/>
                </div>
                <div className="new-expense_control">
                    <label>Price</label>
                    <input type="text" onChange={priceChangeHandler}/>
                </div>

            </div>
                <div className="new-expense_action">
                    <button type="submit">Add Expense</button>

                </div>
        </form>
)
};

export default {
    ExpenseForm :ExpenseForm,
    
};
