import Chart from "../Chart/Chart";


const ExpenseChart = (props)=>{
    const dataPoints = [
        {label:'jan', value : 0},
        {label:'feb', value : 0},
        {label:'mar', value : 0},
        {label:'apr', value : 0},
        {label:'may', value : 0},
        {label:'jun', value : 0},
        {label:'july', value : 0},
        {label:'aug', value : 0},
        {label:'sep', value : 0},
        {label:'oct', value : 0},
        {label:'nov', value : 0},
        {label:'dec', value : 0},   
    ];
    const expensePrice = props.expenses.map((expense)=>{
        return expense.price;
    });
    const maxValue = Math.max(...expensePrice);
    props.expenses.forEach(element => {
        const expenseMonth =element.date.getMonth();
        if(expenseMonth[0]==0){
            expenseMonth = expenseMonth[1];
        }
        dataPoints[expenseMonth].value +=(element.price);
        console.log(dataPoints[expenseMonth].value,'--->');
        console.log(element.price);
    });
  

  return (
    <Chart dataPoints={dataPoints} maxValue={maxValue}></Chart>
  )
};

export default ExpenseChart;
