import Chart from "../Chart/Chart";


const ExpenseChart = (props)=>{
    const dataPoints = [
        {label:'jan', value : 0},
        {label:'feb', value : 0},
        {label:'mar', value : 0},
        {label:'apr', value : 0},
        {label:'jun', value : 0},
        {label:'july', value : 0},
        {label:'aug', value : 0},
        {label:'sep', value : 0},
        {label:'oct', value : 0},
        {label:'nov', value : 0},
        {label:'dec', value : 0},   
    ];
    let index =0;
    const expensePrice = props.expenses.map((expense)=>{
        return expense.price;
    })
    for(const expense in props.expenses){
        const expenseMonth = index;

        dataPoints[expenseMonth].value +=expensePrice[0].price;
        index++;
    }

  return (
    <Chart dataPoints={dataPoints}></Chart>
  )
};

export default ExpenseChart;
