import ExpenseItem from '../Expenses/ExpenseItem';
import Card from '../UI/Card';
import '../Expenses/Expenses.css';
import ExpensesFilter from './ExpenseFilter';
import { useState } from 'react';
function Expenses(props) {
  const [selectedYear, setSelectedYear] = useState('2022');
  const expenseYearSelectionHandler = (expenseYear) => {
    setSelectedYear(expenseYear);
    console.log(expenseYear);
  };
  const filteredExpenses = props.expenses.filter(
    (expense) => {
      return ((expense.date.getFullYear().toString() === selectedYear));
    }
  );
  const date2 = new Date(0, 0, 0);
  return (
    <Card className="expenses">
      <ExpensesFilter selected={selectedYear} onexpenseYearSelection={expenseYearSelectionHandler} />
      {
      /* {
        props.expenses.map(
          (expense) => {
            if(expense.date.getFullYear()==selectedYear){
              return (
                <ExpenseItem key={expense.id} title={expense.title} price={expense.price} date={expense.date} />
                );
              }
              else {
                const date2 = new Date(2000,12,12);
                return (
                  <ExpenseItem key='No Expense' title='No Expense' price='No Expense' date={date2} />
                );
              }
          }
        )
      } */
      }
      {/* **********method-2*********** */}
      {
        (filteredExpenses.length === 0) ? <ExpenseItem key='000' title='No Expense' price='$0' date={date2} />
        : filteredExpenses.map(
          (expense) => {
            return (
              <ExpenseItem key={expense.id} title={expense.title} price={expense.price} date={expense.date} />
              );
            }
            )
          }
          {/* **********method-3*********** */}
          {filteredExpenses.length === 0 && <ExpenseItem key='000' title='No Expense' price='$0' date={date2} />}
          {}






    </Card>
  );

}
export default Expenses;
