import Chart from "../Chart/Chart";


const ExpenseChart = (props)=>{
    const dataPoints = [
        {label:'jan', value : 0},
        {label:'feb', value : 0},
        {label:'mar', value : 0},
        {label:'apr', value : 0},
        {label:'jun', value : 0},
        {label:'july', value : 0},
        {label:'aug', value : 0},
        {label:'sep', value : 0},
        {label:'oct', value : 0},
        {label:'nov', value : 0},
        {label:'dec', value : 0},   
    ];
    const expensePrice = props.expenses.map((expense)=>{
        return expense.price;
    });
    const maxValue = Math.max(...expensePrice);
    props.expenses.forEach(element => {
        const expenseMonth =element.date.getMonth();
        dataPoints[expenseMonth-1].value +=element.price;
    });
  

  return (
    <Chart dataPoints={dataPoints}></Chart>
  )
};

export default ExpenseChart;
