                <ExpenseItem key={expense.id} title={expense.title} price={expense.price} date={expense.date} />
              );
            }
          )
      } */
      }

      {/* **********method-3*********** */}

      {
        /* {filteredExpenses.length === 0 && <ExpenseItem key='000' title='No Expense' price='$0' date={} />}
        {filteredExpenses.length > 0 && filteredExpenses.map(
          (expense) => {
            return (
              <ExpenseItem key={expense.id} title={expense.title} price={expense.price} date={expense.date} />
            );
          }
        )} */
      }

      {/* **********method-4*********** */}

      {expenseContent}





    </Card>
  );

}
export default Expenses;
